:root {
  --radius: 0.75rem;
  --font-sans: 'Space Grotesk', system-ui, sans-serif;

  /* Teal-based palette with warm cream background */
  --background: hsl(40 30% 98%);
  --foreground: hsl(200 25% 15%);
  --card: hsl(0 0% 100%);
  --card-foreground: hsl(200 25% 15%);
  --popover: hsl(0 0% 100%);
  --popover-foreground: hsl(200 25% 15%);

  /* Primary: Deep teal */
  --primary: hsl(174 60% 35%);
  --primary-foreground: hsl(0 0% 100%);
  --primary-light: hsl(174 55% 45%);
  --primary-glow: hsl(174 60% 60%);

  /* Secondary: Soft gray-blue */
  --secondary: hsl(200 15% 94%);
  --secondary-foreground: hsl(200 25% 25%);

  /* Muted: Warm gray */
  --muted: hsl(40 10% 95%);
  --muted-foreground: hsl(200 10% 45%);

  /* Accent: Warm amber */
  --accent: hsl(38 90% 55%);
  --accent-foreground: hsl(30 60% 15%);

  /* Success: Green */
  --success: hsl(152 55% 40%);
  --success-foreground: hsl(0 0% 100%);

  --destructive: hsl(0 72% 55%);
  --border: hsl(200 15% 90%);
  --input: hsl(200 15% 90%);
  --ring: hsl(174 60% 35%);

  /* Chart colors */
  --chart-1: hsl(174 60% 40%);
  --chart-2: hsl(38 90% 55%);
  --chart-3: hsl(200 50% 50%);
  --chart-4: hsl(152 55% 45%);
  --chart-5: hsl(280 50% 55%);

  /* Sidebar */
  --sidebar: hsl(200 25% 18%);
  --sidebar-foreground: hsl(40 20% 95%);
  --sidebar-primary: hsl(174 60% 50%);
  --sidebar-primary-foreground: hsl(200 25% 12%);
  --sidebar-accent: hsl(200 25% 25%);
  --sidebar-accent-foreground: hsl(40 20% 95%);
  --sidebar-border: hsl(200 20% 25%);
  --sidebar-ring: hsl(174 60% 50%);

  /* Custom design tokens */
  --gradient-primary: linear-gradient(135deg, hsl(174 60% 35%), hsl(174 55% 45%));
  --gradient-accent: linear-gradient(135deg, hsl(38 90% 55%), hsl(45 95% 60%));
  --gradient-card: linear-gradient(180deg, hsl(0 0% 100%), hsl(40 20% 98%));
  --shadow-sm: 0 1px 2px hsl(200 25% 15% / 0.05);
  --shadow-md: 0 4px 12px hsl(200 25% 15% / 0.08);
  --shadow-lg: 0 10px 30px hsl(200 25% 15% / 0.12);
  --shadow-primary: 0 4px 20px hsl(174 60% 35% / 0.25);
  --transition-fast: all 0.15s ease;
  --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
}

