                className="input-focus"
              />
            </div>
            <div className="space-y-2">
              <Label htmlFor="kapazitaet">Kapazität *</Label>
              <Input
                id="kapazitaet"
                type="number"
                min={1}
                value={form.kapazitaet || ''}
                onChange={(e) => setForm({ ...form, kapazitaet: parseInt(e.target.value) || 0 })}
                required
                placeholder="z.B. 30"
                className="input-focus"
              />
            </div>
